import { Component, Input } from '@angular/core';
import { QuizService } from "../../shared/services/quiz.service";

@Component({
  selector: 'app-answer',
  templateUrl: './answer.component.html',
  styleUrls: ['./answer.component.scss']
})
export class AnswerComponent {
  @Input() answers: any[] = [];
  @Input() questionId: number = 0;
  isQuizFinished = this.quizService.isQuizFinished;

  constructor(private quizService: QuizService) { }

  getAnswerLetter(j: number) {
    return String.fromCharCode(65 + j);
  }

  addAnswer(answer: string, questionId: number) {
    this.quizService.addAnswer(answer, questionId);
  }

  isAnswerSelected(answer: string, id: number) {
    const isAnswered = this.quizService.playerAnswers.find((a) => a.questionId === id);
    if (!isAnswered) return false;
    return isAnswered.answer === answer;
  }
}
<ul class="answer_container">
  <li *ngFor="let answer of answers; let j = index">
    <button
      [disabled]="isQuizFinished"
      (click)="addAnswer(answer?.answerLabel, questionId)"
      [class.answer_container--selected]="isAnswerSelected(answer?.answerLabel, questionId)"
    >
      <span>{{ getAnswerLetter(j) }}.</span> {{ answer?.answerLabel }}
    </button>
  </li>
</ul>
