import { Component, OnInit } from '@angular/core';
import { QuizService } from '../../shared/services/quiz.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.scss'],
})
export class CategoryComponent implements OnInit {
  categoriesContent: any[] = this.quizService.categoriesContent;
  categoriesContentSearchTmp: any[] = this.categoriesContent;
  constructor(private quizService: QuizService, private router: Router) {}

  ngOnInit(): void {
    this.quizService.getCategories();
  }

  search(text: string) {
    if (!text) {
      this.categoriesContentSearchTmp = this.categoriesContent;
    }
    this.categoriesContentSearchTmp = this.categoriesContent.filter(
      (category) => category?.label.toLowerCase().includes(text.toLowerCase())
    );
  }

  navigateToQuiz(id: number) {
    this.router.navigate(['/quiz', id]);
    this.quizService.resetQuiz();
  }
}
<section class="home">
  <h2 class="home_title">Catégories</h2>
  <input type="text" #filter class="filtre" />
  <button (click)="search(filter.value)">Rechercher</button>
  <section class="quiz_questions-list">
    <div
      class="question_container"
      *ngFor="let content of categoriesContent; let i = index"
    >
      <button (click)="navigateToQuiz(content.id)">
        <h4 class="question_label"><b></b> {{ content?.label }}</h4>
      </button>
    </div>
  </section>
</section>
