import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from "@angular/forms";
import { AuthService } from "../auth.service";
import { Router } from "@angular/router";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loginForm = this.fb.group({
      username: [''],
      password: ['']
    });
  }

  login() {
    this.authService.login(this.loginForm.value).subscribe((user: any) => {
      if (user.length === 0) alert('Erreur dans le pseudo ou le mot de passe');
      this.authService.user = user[0];
      if (!this.authService.user) return;
      this.authService.saveUser();
      this.router.navigate(['/']);
    }, (error) => {
      alert('Erreur dans la requête');
    });
  }
}
<section class="login">
  <form class="login_form" [formGroup]="loginForm" (ngSubmit)="login()">
    <h2>Connexion</h2>
    <div class="login_form-container">
      <input type="text" placeholder="Username" class="input" formControlName="username" />
      <input type="password" placeholder="Password" class="input" formControlName="password" />
    </div>
    <button class="btn">Se connecter</button>
  </form>
  <p class="message">Pas encore parmi nous ? <a routerLink="/register" class="login_register-link">Créer toi un compte !</a></p>
</section>
