import { Component } from '@angular/core';
import { AuthService } from "../auth/auth.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

  constructor(private authService: AuthService) { }

  logout() {
    this.authService.logout();
  }

  get isUserConnected() {
    return this.authService.isUserConnected();
  }

  get getUsername() {
    return this.authService.user?.username || '';
  }
}
<header class="header">
  <h1 routerLink="/">
    <button>Angular Quiz</button>
  </h1>
  <div class="header_user">
    <h2>{{ getUsername }}</h2>
    <button *ngIf="!isUserConnected; else logoutTemplate" class="btn-secondary" routerLink="/login">Se connecter</button>
    <ng-template #logoutTemplate>
      <button class="btn-secondary" (click)="logout()" routerLink="/login">Se déconnecter</button>
    </ng-template>
  </div>
</header>
